package ch03_op;

public class opex10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			long a = 1000000 * 1000000;
			// int * int ---> int
			long b = 1000000 * 1000000L;
			// int * long ----> long
			System.out.println("a="+a);
			System.out.println("b="+b);
		
		
		
		
		
	}

}
